import React from 'react';

const MultipleChoice = (props) => {

    const handleChoiceClick = (e) => {
        const chosen = e.target.value;
        // console.log(chosen);
    }

    const handleNavigation = (direction) => {
        if(direction){
            props.setCurrentQuestionCount(prev => prev - 1 )
        }
        else{
            props.setCurrentQuestionCount(prev => prev + 1)
        }
    }

    return (
        <div className="MultipleChoice-container">
            <div>
                <button className='quizNav' onClick={handleNavigation(-1)}>previous</button>
                <button className='quizNav' onClick={handleNavigation(-1)}>next</button>
            </div>
            <div className='question'>
                {props.question.term}
            </div>
            <div>
                {props.question.choices.map((choice) => {return <button value={choice} onClick={handleChoiceClick} className='mc-btn'>{choice}</button>})}
            </div>
        </div>
    );
};

export default MultipleChoice;
