import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../../styles/quiz-page/QuizQuestions.css';
// import "../components/SavedQuizCard"
// import QuizSideBar from './QuizSidebar';
import MultipleChoice from './MultipleChoice'
import WrittenChoice from './WrittenChoice'

const QuizQuestions = () => {
    const params = new URLSearchParams(location.search);
    const { quizName } = useParams();
    const quizType = params.get('quizType');
    const [questionType, setQuestionType] = useState(quizType == "multipleChoice" || quizType == "written" ? quizType : randomType);
    const [quizData, setQuizData] = useState({
        quizName: '',
        questions: [
            {
                term: '',
                definition: '',
            },
        ],
    });

    const alldefinitions = quizData.questions.map((term, def) => def);
    console.log("Def", alldefinitions);

    const randomType = () => {
        let randomNum = Math.random() * 10 + 1;
        if (randomNum % 2 == 0) {
            return "multipleChoice"
        }
        else {
            return "written"
        }
    }


    useEffect(() => {
        const fetchQuizData = async () => {
            try {
                const response = await fetch(`http://localhost:4000/api/quiz/get-one-quiz/${quizName}`, {
                    method: 'GET',
                    credentials: 'include',
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch quiz data.');
                }
                const data = await response.json();
                const quizData = {
                    quizName: quizName,
                    // map out questions from database. Empty string is default
                    questions: data.map((question) => ({
                        term: question.term || '',
                        definition: question.definition || '',
                    })),
                };
                // Update the state with the retrieved data
                console.log("quizData", quizData);
                setQuizData(quizData);
            } catch (error) {
                console.log('Error fetching quiz data.', error);
            }
        };
        fetchQuizData();

