            }
        }
    })

    useEffect(() => {

    }, [questionType])

    return (
        <div className="QuizQuestions-container">
            {/* <QuizSideBar/> */}
            {questionType == "multipleChoice" ? <MultipleChoice /> : <WrittenChoice />}


        </div>
    );
};

export default QuizQuestions;
