import React from 'react';
import '../styles/NoteList.css';

const NoteList = (props) => {
    return (
        <div className="notes-list">
        <h2>All Notes in {props.selectedFolder}</h2>
        {/*Checks if selected folder exist and that there are notes in that folder*/}
        {props.selectedFolder && props.folderNotes[props.selectedFolder] && props.folderNotes[props.selectedFolder].length > 0 ? (
          //Iterates through array and renders it in a div element
          props.folderNotes[props.selectedFolder].map((note, index) => (
            <div
              key={index}
              // creates a selected class to differentiate between selected and non selected note
              className={`note ${props.selectedNote === note ? 'selected' : ''}`}
              // When note it is clicked, it becomes selected note
              onClick={() => props.setSelectedNote(note)}
            >
              <h3>{note.title}</h3>
            </div>
          ))
        ) : (
          <p>No notes available.</p>
        )}
      </div>
    );
};

export default NoteList;
