import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../../styles/quiz-page/EditQuiz.css';

const EditQuiz = () => {
  const { quizName } = useParams();

  const [quizData, setQuizData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch the quiz data from the server based on quizName
    const fetchQuizData = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/quiz/get-quiz/${quizName}`, {
          method: 'GET',
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error('Failed to fetch quiz data.');
        }

        const data = await response.json();
        setQuizData(data);
      } catch (error) {
        setError('Error fetching quiz data.');
      }
    };

    fetchQuizData();
  }, [quizName]);

  const handleSaveChanges = async () => {
    // Send the updated quiz data to the server to be saved
    try {
      const response = await fetch(`http://localhost:4000/api/quiz/edit-quiz/${quizName}`, {
        method: 'PUT',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(quizData), // Send the updated quiz data to the server
      });

      if (!response.ok) {
        throw new Error('Failed to save changes.');
      }

      // Redirect to the saved quizzes page after successful update
      window.location.href = '/saved-quizzes';
    } catch (error) {
      console.log('Error saving changes:', error);
      setError('Failed to save changes.');
    }
  };

  // Rest of the code (handleAddQuestion, handleRemoveQuestion, handleChange) remains the same

  if (!quizData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="edit-quiz">
      {/* ... */}
    </div>
  );
};

export default EditQuiz;
