import React, { useState } from 'react';
import 'react-quill/dist/quill.bubble.css';
import '../../styles/quiz-page/CustomQuizpage.css';
import CustomQuizCard from './CustomQuizCard';

const CustomQuizPage = () => {

    const [quizTitle, setQuizTitle] = useState("");
    const [quizContents, setQuizContents] = useState([{term:"", definition:""}]);

    console.log(quizContents);
    const addCard = () =>{
        setQuizContents(currentQuizContents => {
            return currentQuizContents.concat({term:"",def:""})
        })
    }

    const updateTitle = (e) => {
        setQuizTitle(e.target.value);
    }

    const handleQuizSave = () => {
        
    }

    return (
        <div className="customQuizpage-container">
            <div className="left-padding"></div>
            <div className='customQuiz-right'>
                <div className='createQuizTitle'>Create Custom Quiz</div>
                
                <label htmlFor='quizTitle'>Title: </label>
                <input
                    id="quizTitle"
                    type="text"
                    placeholder="Enter Title"
                    value={quizTitle}
                    onChange={updateTitle}
                />

                {quizContents.map((e, index) => {
                    return <CustomQuizCard term={e.term} def={e.definition} index={index} quizContents={quizContents} setQuizContents={setQuizContents}/>
                })}

                <button onClick={addCard}>Add Card</button>
                <button onClick={handleQuizSave}>Save Quiz</button>
            </div>
        </div>
    );
};

export default CustomQuizPage;
