import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../../styles/quiz-page/EditQuiz.css';

const EditQuiz = () => {
  const { quizName } = useParams();
  const navigate = useNavigate();

  const [quizData, setQuizData] = useState(null);


  useEffect(() => {
    // Fetch the quiz data from the server based on quizName
    const fetchQuizData = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/quiz/get-quiz/${quizName}`, {
          method: 'GET',
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error('Failed to fetch quiz data.');
        }

        const data = await response.json();
        setQuizData(data);
      } catch (error) {
        console.log('Error fetching quiz data.',error);
      }
    };

    fetchQuizData();
  }, [quizName]);

  const handleSaveChanges = async () => {
    // Send the updated quiz data to the server to be saved
    try {
      const response = await fetch(`http://localhost:4000/api/quiz/edit-quiz/${quizName}`, {
        method: 'PUT',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(quizData), // Send the updated quiz data to the server
      });

      if (!response.ok) {
        throw new Error('Failed to save changes.');
      }

      navigate('/saved-quizzes'); // Redirect to the saved quizzes page after successful update
    } catch (error) {
      console.log('Error saving changes:', error);
    }
  };

  const handleAddQuestion = () => {
    // Add a new question to the quizData
    setQuizData((prevData) => ({
      ...prevData,
      questions: [
        ...prevData.questions,
        {
          question: '',
          answer: '',
        },
      ],
    }));
  };

  const handleRemoveQuestion = (index) => {
    // Remove a question from the quizData based on its index
    setQuizData((prevData) => ({
      ...prevData,
      questions: prevData.questions.filter((_, i) => i !== index),
    }));
  };

  const handleChange = (event, index) => {
    // Update the quizData state when user makes changes to the quiz
    const { name, value } = event.target;
    setQuizData((prevData) => ({
      ...prevData,
      questions: prevData.questions.map((question, i) =>
        i === index ? { ...question, [name]: value } : question
      ),
    }));
  };

  if (!quizData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="edit-quiz">
      <h2>Edit Quiz</h2>
      <input
        type="text"
        name="quizName"
        value={quizData.quizName}
        onChange={(e) => handleChange(e, 0)} // Handle quiz name change (index 0 represents the quiz name)
        placeholder="Quiz Name"
      />
      <ul>
        {quizData.questions.map((question, index) => (
          <li key={index}>
            <input
              type="text"
              name={`question${index + 1}`}
              value={question.question}
              onChange={(e) => handleChange(e, index + 1)} // Handle question change
              placeholder={`Question ${index + 1}`}
            />
            <input
              type="text"
              name={`answer${index + 1}`}
              value={question.answer}
              onChange={(e) => handleChange(e, index + 1)} // Handle answer change
              placeholder={`Answer ${index + 1}`}
            />
            <button onClick={() => handleRemoveQuestion(index)}>Remove</button>
          </li>
        ))}
      </ul>
      <button onClick={handleAddQuestion}>Add Question</button>
      <button onClick={handleSaveChanges}>Save Changes</button>
    </div>
  );
};

export default EditQuiz;
