import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const StartQuizPage = () => {
  const { quizName } = useParams();
  const navigate = useNavigate();

  const [quizData, setQuizData] = useState({
    quizName: '',
    questions: [],
    quizType: null,
  });

  useEffect(() => {
    const fetchQuizData = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/quiz/get-one-quiz/${quizName}`, {
          method: 'GET',
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error('Failed to fetch quiz data.');
        }

        const data = await response.json();
        const quizData = {
          quizName: quizName,
          questions: data.map((question) => ({
            term: question.term || '',
            definition: question.definition || '',
          })),
          quizType: null, // Initialize the quizType as null initially
        };
        setQuizData(quizData);
      } catch (error) {
        console.log('Error fetching quiz data.', error);
      }
    };
    fetchQuizData();
  }, [quizName]);

  const handleQuizTypeSelection = (quizType) => {
    setQuizData((prevData) => ({
      ...prevData,
      quizType: quizType,
    }));
    handleStartQuiz(quizType); // Start the quiz when the user selects a quiz type
  };

  const handleStartQuiz = (quizType) => {
    // Check if the user has selected a quiz type
    if (!quizType) {
      alert('Please select a quiz type before starting the quiz.');
    } else {
      // Start the quiz here (you can add the logic to navigate to the quiz questions page or render the questions directly)
      // For demonstration purposes, let's log the quiz data and navigate back to the homepage
      console.log('Quiz Data:', quizData);
      navigate('/'); // Navigate to the homepage (you can change this to the quiz questions page)
    }
  };

  return (
    <div>
      <h2>Start Quiz: {quizData.quizName}</h2>
      <div>
        <p>Select Quiz Type:</p>
        <button
          onClick={() => handleQuizTypeSelection('multipleChoice')}
          className={quizData.quizType === 'multipleChoice' ? 'selected' : ''}
        >
          Multiple Choice
        </button>
        <button
          onClick={() => handleQuizTypeSelection('written')}
          className={quizData.quizType === 'written' ? 'selected' : ''}
        >
          Written Question
        </button>
        <button
          onClick={() => handleQuizTypeSelection('both')}
          className={quizData.quizType === 'both' ? 'selected' : ''}
        >
          Both
        </button>
      </div>
    </div>
  );
};

export default StartQuizPage;
