import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../../styles/quiz-page/EditQuiz.css';

const EditQuiz = () => {
  const { quizName } = useParams();
  const navigate = useNavigate();

  const [quizData, setQuizData] = useState({
    quizName: '',
    questions: [
      {
        term: '',
        definition: '',
      },
    ],
  });

  useEffect(() => {
    // Fetch the quiz data from the server based on quizName
    const fetchQuizData = async () => {
        try {
          const response = await fetch(`http://localhost:4000/api/quiz/get-one-quiz/${quizName}`, {
            method: 'GET',
            credentials: 'include',
          });
      
          if (!response.ok) {
            throw new Error('Failed to fetch quiz data.');
          }
      
          const data = await response.json();
      

            const quizData = {
              quizName:quizName, // Update this based on your use case
              questions: data.map((question) => ({
                term: question.term || '', // Provide an empty string as default value
                definition: question.definition || '', // Provide an empty string as default value
              })),
            };
            // Update the state with the retrieved data
            setQuizData(quizData);

        } catch (error) {
          console.log('Error fetching quiz data.', error);
        }
      };

    fetchQuizData();
  }, [quizName]);

  const handleChange = (event, index, field) => {
    const { name, value } = event.target;
    setQuizData((prevData) => ({
      ...prevData,
      questions: prevData.questions.map((termData, i) =>
        i === index ? { ...termData, [field]: value } : termData
      ),
    }));
  };

  const handleAddQuestion = () => {
    setQuizData((prevData) => ({
      ...prevData,
      questions: [
        ...prevData.questions,
        {
          term: '',
          definition: '',
        },
      ],
    }));
  };

  const handleRemoveQuestion = (index) => {
    setQuizData((prevData) => ({
      ...prevData,
      questions: prevData.questions.filter((_, i) => i !== index),
    }));
  };

  const handleSaveChanges = async () => {
    try {
      const response = await fetch(`http://localhost:4000/api/quiz/edit-quiz/${quizName}`, {
        method: 'PUT',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(quizData),
      });
  
      if (!response.ok) {
        throw new Error('Failed to save changes.');
      }
  
      const data = await response.json();
      console.log('Server Response:', data); // Log the response from the server
  
      navigate('/quiz');
    } catch (error) {
      console.log('Error saving changes:', error);
    }
  };
  

  if (!quizData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="edit-quiz">
      <h2>Edit Quiz</h2>
      <input
        type="text"
        name="quizName"
        value={quizData.quizName}
        onChange={(e) => handleChange(e, 0, 'term')} // Pass 'term' as the third argument
        placeholder="Quiz Name"
      />
      <ul>
        {quizData.questions &&
          quizData.questions.map((termData, index) => (
            <li key={index}>
              <input
                type="text"
                name={`term${index + 1}`}
                value={termData.term || ''}
                onChange={(e) => handleChange(e, index, 'term')} // Pass 'term' as the third argument
                placeholder={`Term ${index + 1}`}
              />
              <textarea
                name={`definition${index + 1}`}
                value={termData.definition || ''}
                onChange={(e) => handleChange(e, index, 'definition')} // Pass 'definition' as the third argument
                placeholder={`Definition ${index + 1}`}
              />
  
              <button onClick={() => handleRemoveQuestion(index)}>Remove</button>
            </li>
          ))}
      </ul>
      <button onClick={handleAddQuestion}>Add Question</button>
      <button onClick={handleSaveChanges}>Save Changes</button>
    </div>
  );
};

export default EditQuiz;
