import React from 'react';

const Sidebar = (props) => {

    const addOnClick = (e) =>{
        e.preventDefault();
        e.target.style.display = "none";
        e.target.nextElementSibling.firstChild.style.display = "";
    }

    return (
        <div className="sidebar-container" id={props.myId}>
            <ul style={{listStyleType: "none"}}>
                {props.data.map((item) =>{
                    return(
                        <li>{item.section}</li>
                    )
                }
                    
                )}
            </ul>
            <button id="sidebarAdd" onClick={addOnClick}> Add </button>
                
            <form>
                <input type = "text" id="sidebarInput" style={{display: "none"}}/>
            </form>
        </div>
    );
};

export default Sidebar;
