import React, { useState, useEffect } from 'react';

const WrittenChoice = (props) => {
  const { question, currentQuestionIndex, setAllUserAns, allUserAns } = props;
  const [userAnswer, setUserAnswer] = useState('');

  // Update the local state if the user answer changes from props
  useEffect(() => {
    setUserAnswer(allUserAns[currentQuestionIndex] || '');
  }, [allUserAns, currentQuestionIndex]);

  const handleInputChange = (event) => {
    setUserAnswer(event.target.value);
  };

  // Save the user's answer to the state array in the parent component
  useEffect(() => {
    const newUserAnswers = [...allUserAns];
    newUserAnswers[currentQuestionIndex] = userAnswer;
    setAllUserAns(newUserAnswers);
  }, [userAnswer, currentQuestionIndex, setAllUserAns]);

  return (
    <div className="WrittenChoice-container">
      <h3>{question.term}</h3>
      <div className="answer-input">
        <input type="text" value={userAnswer} onChange={handleInputChange} />
      </div>
    </div>
  );
};

export default WrittenChoice;
