import React, { useState, useEffect } from 'react';
import '../../styles/quiz-page/SavedQuizzes.css'

const SavedQuizzes = () => {
  const [allQuizzes, setAllQuizzes] = useState({});

  useEffect(() => {
    const getQuizzes = async () => {
      try {
        const response = await fetch('http://localhost:4000/api/quiz/my-quizzes', {
          method: 'GET',
          credentials: 'include'
        });

        if (!response.ok) {
          throw new Error('Failed to fetch quizzes.');
        }

        const data = await response.json();
        setAllQuizzes(data);
      } catch (error) {
        console.log('Error getting quizzes:', error);
      }
    };

    getQuizzes();
    console.log(allQuizzes);
  }, []);

  // Function to delete a quiz
  const handleDeleteQuiz = (quizName) => {
    // Your code to delete the quiz data goes here
    // For example, you can use fetch() to make an API call to delete the quiz
    console.log('Deleting quiz with name:', quizName);
    // Update the state to remove the deleted quiz from the list
    setAllQuizzes((prevQuizzes) => {
      const updatedQuizzes = { ...prevQuizzes };
      delete updatedQuizzes[quizName];
      return updatedQuizzes;
    });
  };

  return (
    <div className="saved-quizzes">
      <h2>Saved Quizzes</h2>
      <ul>
        {Object.keys(allQuizzes).map((quizName) => {
          const quiz = allQuizzes[quizName];
          return (
            <li key={quizName}>
              <span>{quiz.name}</span>
              <button onClick={() => handleDeleteQuiz(quizName)}>Delete</button>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default SavedQuizzes;
