import React from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import "../styles/Schedule.css"

const Schedule = (props) => {

    const onEventClick = (e) => {
        const eventTitle = e.event.title;
        const eventStart = e.event.start;
        const eventEnd = e.event.end;

        const formattedEventStart = String(eventStart.getMonth()+1).padStart(2,'0') + "-" + 
            String(eventStart.getDate()).padStart(2,"0") + "-" +
            String(eventStart.getFullYear());

        const formattedEventEnd = String(eventEnd.getMonth()+1).padStart(2,'0') + "-" + 
        String(eventEnd.getDate()).padStart(2,"0") + "-" +
        String(eventEnd.getFullYear());
        
        

    }

    return (
        <FullCalendar
            plugins={[dayGridPlugin]}
            initialView='dayGridMonth'
            events={
                props.myEvents
            }
            selectable={true}
            eventClick={onEventClick}
        />
    );
};

export default Schedule;
